import React from "react";
import Data from './Data'
import Card from './Card'

export default function Exercise() {
  let Cards = Data.map(item => 
    <Card 
      key={item.id}
      item={item}
    />
  )

  const [randomCard, setRandomCard] = React.useState(Cards)
  function handleClick() {
    setRandomCard()
  }


return (
  <div>
    <div className="main-content">{Cards[Math.floor(Math.random() * Cards.length)]}</div>
       <button className="random-button" onClick={handleClick}>Roll the roulette!</button>
  </div>
)

